import React, { useReducer } from 'react'
import PageTitle from '../../components/layout/PageTitle'


const initialState = {
    cart: [],
    products: [],
    user: null,
    number: 0
}


function sum(n1){
 return initialState.number * n1
}

function reducer(state, action) {
    switch (action.type) {
        case 'Number2Add':
            return { ...state, number: state.number + 2}
        case 'login':
            return { ...state, user: { name: action.payload } }
       case 'dividir':
           return { ...state, number: state.number /25}
           case 'Multiplicar Valor':
            return { ...state, number: parseInt(state.number) *  sum(7)}
        default:
            return state
    }
}

const UseReducer = (props) => {

    const [state, dispatch] = useReducer(reducer, initialState)

    return (
        <div className="UseReducer">
            <PageTitle
                title="Hook UseReducer"
                subtitle="Uma outra forma de ter estado em componentes funcionais!"
            />


            <div className="center">

            {state.user ?  
            <span className="text">{state.user.name}</span> 
            : <span className="text">Usuario n√£o Encontrado</span>

            }

                    <span className="text">{state.number}</span>
                    <button className="btn"
                        onClick={() => dispatch({ type: 'Number2Add' })}>+2
                    </button>
                </div>
                <button className="btn"
                    onClick={() => dispatch({ type: 'login', payload:'Maria' })}>Loguin
                </button>
                <button className="btn"
                    onClick={() => dispatch({ type: 'dividir' })}>dividir
                </button>
                <button className="btn"
                    onClick={() => dispatch({ type: 'Multiplicar Valor' })}>Multiplicar Valor
                </button>
                <div>
            </div>
        </div>
    )
}

export default UseReducer
